BEGIN TRANSACTION;
CREATE TABLE assets (
	id VARCHAR(36) NOT NULL, 
	symbol VARCHAR(10) NOT NULL, 
	name VARCHAR(100) NOT NULL, 
	asset_type VARCHAR(20) NOT NULL, 
	exchange VARCHAR(50), 
	currency VARCHAR(10), 
	min_lot_size FLOAT, 
	max_leverage FLOAT, 
	spread FLOAT, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	UNIQUE (symbol)
);
CREATE TABLE challenges (
	id VARCHAR(36) NOT NULL, 
	user_id VARCHAR(36) NOT NULL, 
	plan_type VARCHAR(20) NOT NULL, 
	initial_balance FLOAT NOT NULL, 
	current_balance FLOAT NOT NULL, 
	status VARCHAR(20) NOT NULL, 
	max_daily_loss_pct FLOAT, 
	max_total_loss_pct FLOAT, 
	profit_target_pct FLOAT, 
	start_date DATETIME, 
	end_date DATETIME, 
	payment_status VARCHAR(20), 
	payment_method VARCHAR(20), 
	created_at DATETIME, 
	updated_at DATETIME, daily_start_balance FLOAT DEFAULT 0.0, last_reset_date DATETIME, failed_reason TEXT, completed_at TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE leaderboards (
	id VARCHAR(36) NOT NULL, 
	user_id VARCHAR(36) NOT NULL, 
	username VARCHAR(80) NOT NULL, 
	profit_percentage FLOAT NOT NULL, 
	rank INTEGER NOT NULL, 
	month VARCHAR(7) NOT NULL, 
	created_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE payments (
	id VARCHAR(36) NOT NULL, 
	user_id VARCHAR(36) NOT NULL, 
	amount FLOAT NOT NULL, 
	currency VARCHAR(3) NOT NULL, 
	method VARCHAR(20) NOT NULL, 
	status VARCHAR(20) NOT NULL, 
	transaction_id VARCHAR(100), 
	timestamp DATETIME, 
	description TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	UNIQUE (transaction_id)
);
CREATE TABLE trades (
	id VARCHAR(36) NOT NULL, 
	trade_type VARCHAR(10) NOT NULL, 
	symbol VARCHAR(10) NOT NULL, 
	quantity INTEGER NOT NULL, 
	entry_price FLOAT NOT NULL, 
	exit_price FLOAT, 
	entry_time DATETIME, 
	exit_time DATETIME, 
	pnl FLOAT, 
	status VARCHAR(20), 
	stop_loss FLOAT, 
	take_profit FLOAT, 
	user_id VARCHAR(36) NOT NULL, 
	trading_account_id VARCHAR(36) NOT NULL, 
	asset_id VARCHAR(36), 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id), 
	FOREIGN KEY(trading_account_id) REFERENCES trading_accounts (id), 
	FOREIGN KEY(asset_id) REFERENCES assets (id)
);
CREATE TABLE trading_accounts (
	id VARCHAR(36) NOT NULL, 
	account_number VARCHAR(20) NOT NULL, 
	balance FLOAT NOT NULL, 
	equity FLOAT NOT NULL, 
	leverage FLOAT NOT NULL, 
	risk_level VARCHAR(20), 
	status VARCHAR(20), 
	created_at DATETIME, 
	updated_at DATETIME, 
	user_id VARCHAR(36) NOT NULL, 
	PRIMARY KEY (id), 
	UNIQUE (account_number), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE ts_trades (
	id VARCHAR(36) NOT NULL, 
	challenge_id VARCHAR(36) NOT NULL, 
	symbol VARCHAR(10) NOT NULL, 
	trade_type VARCHAR(10) NOT NULL, 
	quantity INTEGER NOT NULL, 
	entry_price FLOAT NOT NULL, 
	exit_price FLOAT, 
	profit_loss FLOAT, 
	timestamp DATETIME, 
	is_closed BOOLEAN, user_id VARCHAR(36), created_at DATETIME, updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(challenge_id) REFERENCES challenges (id)
);
CREATE TABLE users (
	id VARCHAR(36) NOT NULL, 
	username VARCHAR(80) NOT NULL, 
	email VARCHAR(120) NOT NULL, 
	password_hash VARCHAR(255) NOT NULL, 
	role VARCHAR(20), 
	balance FLOAT NOT NULL, 
	created_at DATETIME, 
	last_login DATETIME, 
	is_active BOOLEAN, country TEXT, 
	PRIMARY KEY (id), 
	UNIQUE (username), 
	UNIQUE (email)
);
CREATE INDEX idx_challenge_user ON challenges(user_id);
CREATE INDEX idx_trade_challenge ON ts_trades(challenge_id);
CREATE INDEX idx_trade_user ON ts_trades(user_id);
COMMIT;
